    contents: [{ uri: uri.href, mimeType: 'application/json', text: JSON.stringify(stats) }]
  })
);

// Resource template for dynamic URIs
server.resource(
  'gno://doc/{path}',
  new ResourceTemplate('gno://doc/{path}', { list: listDocs }),
  async (uri, { path }) => ({
