```typescript
export function handleSync(args: GnoSyncInput, ctx: ToolContext): Promise<ToolResult> {
  return runTool(ctx, 'gno_sync', async () => {
    return withWriteLock(ctx, async () => {
      const collections = args.collection
        ? [args.collection.toLowerCase()]
        : ctx.collections.map(c => c.name);

      if (args.collection) {
        const exists = ctx.collections.some(c => c.name === args.collection.toLowerCase());
        if (!exists) throw new McpError('NOT_FOUND', `Collection: ${args.collection}`);
      }

      const options = {
        gitPull: args.gitPull ?? false,
        runUpdateCmd: args.runUpdateCmd ?? false,
      };

      const jobId = await ctx.jobManager.startJob('sync', async () => {
        if (args.collection) {
          return ctx.syncService.syncCollection(args.collection.toLowerCase(), options);
        }
        return ctx.syncService.syncAll(options);
      });

      return { jobId, collections, status: 'started', options };
    });
  });
}
```

