import { applyConfigChange } from "../../core/config-mutation";
import { removeCollection } from "../../collection/remove";

export function handleRemoveCollection(args: GnoRemoveCollectionInput, ctx: ToolContext): Promise<ToolResult> {
  return runTool(ctx, 'gno_remove_collection', async () => {
    const name = args.collection.toLowerCase();

    const result = await applyConfigChange(ctx, () =>
      removeCollection(ctx.config, name)
    );

    if (result.status === 'NOT_FOUND') {
      throw new McpError('NOT_FOUND', `Collection not found: ${args.collection}`);
