### Component Structure
```tsx
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Loader } from "@/components/ai-elements/loader";

export default function Browse({ navigate }: { navigate: (to: string) => void }) {
  const [collections, setCollections] = useState<Collection[]>([]);
  const [selectedCollection, setSelectedCollection] = useState<string | null>(null);
  const [documents, setDocuments] = useState<Document[]>([]);
  const [limit, setLimit] = useState(20);
  const [hasMore, setHasMore] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  // Get collection from URL param
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const col = params.get("collection");
    if (col) setSelectedCollection(col);
  }, []);

  // Fetch collections on mount
  useEffect(() => {
    fetchCollections().then(setCollections).finally(() => setIsLoading(false));
  }, []);

  // Fetch documents when collection changes
  useEffect(() => {
    if (!selectedCollection) return;
    setIsLoading(true);
    fetchDocuments(selectedCollection, limit)
      .then((res) => {
        setDocuments(res.documents);
        setHasMore(res.hasMore);
      })
      .finally(() => setIsLoading(false));
    // Update URL
    const url = `/browse?collection=${encodeURIComponent(selectedCollection)}`;
    window.history.replaceState({}, "", url);
  }, [selectedCollection, limit]);

  return (
    <div className="max-w-4xl mx-auto p-6">
      {/* Collection selector */}
      <header className="mb-6">
        <h1 className="font-serif text-2xl mb-4">Browse Documents</h1>
        <Select
          value={selectedCollection ?? ""}
          onValueChange={setSelectedCollection}
        >
          <SelectTrigger className="w-64">
            <SelectValue placeholder="Select a collection" />
          </SelectTrigger>
          <SelectContent>
            {collections.map((col) => (
              <SelectItem key={col.name} value={col.name}>
                {col.name} ({col.documentCount})
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </header>

      {/* Document list */}
      {isLoading ? (
        <div className="flex justify-center py-12">
          <Loader size={32} />
        </div>
      ) : !selectedCollection ? (
        <EmptyState message="Select a collection to browse documents" />
      ) : documents.length === 0 ? (
        <EmptyState message="No documents in this collection" />
      ) : (
        <>
          <div className="space-y-2">
            {documents.map((doc) => (
              <DocumentRow
                key={doc.uri}
                document={doc}
                onClick={() => navigate(`/doc?uri=${encodeURIComponent(doc.uri)}`)}
              />
            ))}
          </div>

          {/* Load more */}
          {hasMore && (
            <div className="mt-6 text-center">
              <Button
                variant="outline"
                onClick={() => setLimit((prev) => prev + 20)}
              >
                Load more documents
              </Button>
            </div>
          )}
        </>
      )}
    </div>
  );
}
